DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `autoCreateFixedAssetDepreciation`(IN _code VARCHAR(20))
BEGIN
	declare _id int;
	select id into _id from fixed_asset_depreciation where code=_code;
	if _id is null then
		insert into fixed_asset_depreciation(code, created_date, note) values(_code, sysdate(),'');
		SELECT LAST_INSERT_ID() INTO _id;
	
		insert into fixed_asset_depreciation_detail(fixed_asset_depreciation_id,fixed_asset_id,unit_id,quantity,is_calculate)
		select _id, a.id, a.unit_id, a.quantity, a.status
		from fixed_asset as a
		where a.is_closed=0;
	end if;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `calculateCustomerCommission` */

/*!50003 DROP PROCEDURE IF EXISTS  `calculateCustomerCommission` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `calculateCustomerCommission`(in _customer_id int, in _weight double, in _return_shell int, out _commission double)
BEGIN
	SELECT SUM(COALESCE(dd.commission*_weight,0) + COALESCE(d.shell_gas_commission*_return_shell,0)) INTO _commission
	FROM discount_oil AS d, discount_oil_customer AS dc,  discount_oil_detail AS dd
	WHERE d.id=dc.discount_id AND d.id=dd.discount_id AND dc.customer_id=_customer_id AND dd.commission_from<=_weight AND dd.commission_to>_weight;
    END */$$
DELIMITER ;

