			GROUP BY i.created_date, idet.solar_id
		) AS tbl
		LEFT JOIN
		(
			SELECT _from_date AS created_date, o.id AS solar_id, SUM(COALESCE(in_stock,0)) AS opening_stock
			FROM solar AS o
			LEFT JOIN (
