				FROM solar_in_stock
				WHERE DATEDIFF(`day`, _from_date) > 0 AND DATEDIFF(`day`, _to_date) <= 0 
					AND _organization_ids LIKE CONCAT('%,',organization_id,',%') AND store_id=_store_id	
