--				AND i_det.vehicle_in_id=i.id
--			GROUP BY i_det.shell_id, i.created_date
			UNION ALL
			SELECT f_det.shell_id AS shell_vendor_id, f.created_date, -SUM(f_det.quantity) AS quantity
