		WHILE _date_diff>0 DO
			SET _in_stock_id=0;
			-- lpg_in_stock
			SELECT id INTO _in_stock_id FROM lpg_in_stock WHERE DATEDIFF(`day`,_date)=0 AND organization_id=_organization_id AND vendor_id=_import_vendor_id;
