			IF _kind=1 THEN
				SET _in_stock_id=0;
				-- vendor_in_stock
				SELECT id INTO _in_stock_id FROM vendor_in_stock WHERE DATEDIFF(`day`,_date)=0 AND organization_id=_organization_id AND vendor_id=_vendor_id;
